import React from 'react';
import { Button, Text, View, StyleSheet, Pressable } from 'react-native';

//COMPONENTS
import ButtonContainer from '../components/ButtonContainer';
import SliderPopular from '../components/sliders/SliderPopular';
import SliderEnergy from '../components/sliders/SliderEnergy';
import SliderDance from '../components/sliders/SliderDance';
import SliderInstrument from '../components/sliders/SliderInstrument';
import { SliderProvider } from '../context/sliderContext';

//API call
const SpotifyWebApi = require('spotify-web-api-node');
const SpotifyApi = new SpotifyWebApi();

export default function PlaylistScreen({ navigation }) {

  const playListReady = () => {
    navigation.navigate('PlaylistScreen');
  };

  return (
    <>
      <View style={styles.container}>

        <ButtonContainer />
        {/* //CREATE TOGGLES */}
        <SliderProvider>

        <SliderPopular />
        <SliderEnergy />
        <SliderDance />
        <SliderInstrument />
        </SliderProvider>
        <Button
          title="create playlist"
          onPress={() => playListReady()}
        ></Button>
        <View style={styles.ageContainer}></View>
      </View>
    </>
  );
}

const styles = StyleSheet.create({
  container: {
    paddingVertical: 20,
    flex: 1,
    alignItems: 'center',
    justifyContent: 'space-around',
    backgroundColor: '#d3beed',

  },
  ageContainer: {
    flex: 1,
  },
  buttonContainer: {
    paddingVertical: 30,
    backgroundColor: '#d3beed',
    width: '100%',
    alignItems: 'center',
    justifyContent: 'center',
    flex: 1,
  },

});
